format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set items_label_doc = frappe.get_doc('Sales Invoice PDF Print Format-Duty and Taxes') %}
{% set items_label = items_label_doc.items_for_dt_pf if items_label_doc.items_for_dt_pf else None %}

<div style="font-size:15pt;text-align:center;width:100%;font-weight:bold;color:#23262D;">IMPORT DUTY & TAXES REIMBURSEMENT</div>

<div style="width:100%;display:flex;">
    <div style="width:50%;">
        <table style="width:100%;color:#23262D;">
            <tr>
                <td style="padding:0 !important;">
                    <span style="font-size:8pt;">Receipt No: {{ doc.name }}</span>
                </td>
            </tr>
            <tr>
                <td style="padding:0 !important;">
                    <span style="font-size:12pt;font-weight:bold;">{{ doc.customer }}</span>
                </td>
            </tr>

        </table>
    </div>

    <div style="width:50%;text-align:right;float:right;">
        <table style="width:100%;color:#23262D;">
            <tr>
                <td style="padding:0 !important;">
                    <span style="font-size:8pt;font-weight:bold;">Date:{{doc.end_date}}</span>
                </td>
            </tr>
        </table>
    </div>
</div>
<br>
<div style="color:#23262D;font-size:8pt;text-align:center;width:100%;font-weight:bold;">
    Reimbursement Statement For the dated of {{ frappe.format_date(doc.start_date) }} to {{ frappe.format_date(doc.end_date) }}
</div>

<div>
    <table style = "width:100%;border:1px solid #E0E1E6;font-color:#23262D;font-size:6pt;text-align:center;">
    <tr>
        <td style=" font-weight:bold; font-size:6pt; border:1px solid #E0E1E6;">S.No</td>
        <td style=" font-weight:bold; font-size:6pt; border:1px solid #E0E1E6;">{{ doc.date_type }}</td>
        <td style=" font-weight:bold; font-size:6pt; border:1px solid #E0E1E6;">UPS Tracking No</td>
        <td style=" font-weight:bold; font-size:6pt; border:1px solid #E0E1E6;">Shipper Name</td>
        {% if items_label %}
            {% for row in items_label %}
                <td style="font-weight:bold; font-size:6pt; border:1px solid #E0E1E6;">{{ row.item }}</td>
            {% endfor %}
        {% endif %}


        <td style=" font-weight:bold; font-size:6pt; border:1px solid #E0E1E6;">Total Amount</td>
    </tr>
    {%
        set date_field_map_with_dt = {
            "Posting Date": "posting_date",
            "Shipped Date": "custom_date_shipped",
            "Import Date": "custom_import_date",
            "Arrival Date": "custom_arrival_date"
        }
    %}
    {% set selected_field = date_field_map_with_dt[doc.date_type] %}

    {% if doc.all_sales_invoices %}
        {% for inv in doc.all_sales_invoices.split(",") %}
            {% set inv_name = inv.strip() %}
            {% set inv_doc = frappe.get_doc('Sales Invoice', inv_name) %}

            <tr>
                <td style="text-align:center; padding:2px !important; font-size:6pt; border:1px solid #E0E1E6;">
                    {{ loop.index }}
                </td>
                <td style="text-align:center; padding:2px !important; font-size:6pt; border:1px solid #E0E1E6;">
                    {{ inv_doc[selected_field] }}
                </td>
                <td style="text-align:center; padding:2px !important; font-size:6pt; border:1px solid #E0E1E6;">
                    {{ inv_doc.custom_tracking_number }}
                </td>
                <td style="text-align:center; padding:2px !important; font-size:6pt; border:1px solid #E0E1E6;">
                    {{ inv_doc.custom_shipper_name }}
                </td>
                {% if items_label %}
                    {% for row in items_label %}
                        <td style="text-align:right; padding:2px !important; font-size:6pt; border:1px solid #E0E1E6;">
                            {% for si_item in inv_doc.items %}
                                {% if si_item.item_code == row.item %}
                                    {{ si_item.amount }}
                                {% endif %}
                            {% endfor %}
                        </td>
                    {% endfor %}
                {% endif %}
                <td style="text-align:right; padding:2px !important; font-size:6pt; border:1px solid #E0E1E6;">
                    {{ inv_doc.grand_total }}
                </td>
            </tr>
        {% endfor %}
    {% endif %}



    </table>
</div>"}]
